package com.oracle.lad.z2h.controller;

import com.oracle.lad.z2h.model.Album;
import com.oracle.lad.z2h.repository.AlbumRepository;
import com.oracle.lad.z2h.util.Constant;

import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Paths;
import java.util.Base64;
import java.util.List;
import java.util.Optional;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.CrossOrigin;
import org.springframework.web.bind.annotation.DeleteMapping;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PathVariable;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.PutMapping;
import org.springframework.web.bind.annotation.RequestBody;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RestController;

/**
 *
 * @author rujay
 */
@RestController
@CrossOrigin(origins = "*", allowedHeaders = "*")
@RequestMapping(Constant.API_ALBUM_URL)
public class AlbumController {
    private final AlbumRepository repository;

    @Autowired
    public AlbumController(AlbumRepository repository) {
        this.repository = repository;
    }

    @GetMapping()
    public ResponseEntity<?> getAll() {
        try {
            List<Album> list = repository.findAll();
            list.stream().forEach((p) -> {
                try {
                    if (p.getFoto() != null) {
                        byte[] image = Files.readAllBytes(Paths.get(Constant.PATH_IMAGE_DISK, p.getFoto()));
                        String encoded = Base64.getEncoder().encodeToString(image);
                        p.setFotoB64(encoded);
                    } else {
                        p.setFotoB64(
                                "");
                    }
                } catch (IOException e) {
                    p.setFotoB64(
                            "");
                }
            });

            return new ResponseEntity<>(repository.findAll(), HttpStatus.OK);
        } catch (Exception ex) {
            return new ResponseEntity<>(ex, HttpStatus.INTERNAL_SERVER_ERROR);
        }
    }

    @PostMapping()
    public ResponseEntity<?> create(@RequestBody Album payload) {
        try {
            repository.save(payload);
            return new ResponseEntity<>(payload, HttpStatus.OK);
        } catch (Exception ex) {
            return new ResponseEntity<>(ex, HttpStatus.INTERNAL_SERVER_ERROR);
        }
    }

    @PutMapping("/{id}")
    public ResponseEntity<?> update(@PathVariable Integer id, @RequestBody Album payload) {
        try {
            Optional<Album> compare = repository.findById(id);
            if (compare != null && compare.isPresent() && compare.get().getId().equals(payload.getId())) {
                repository.save(payload);
                return new ResponseEntity<>(payload, HttpStatus.OK);
            } else {
                return new ResponseEntity<>(payload, HttpStatus.CONFLICT);
            }
        } catch (Exception ex) {
            return new ResponseEntity<>(ex, HttpStatus.INTERNAL_SERVER_ERROR);
        }
    }

    @DeleteMapping("/{id}")
    public ResponseEntity<?> delete(@PathVariable Integer id) {
        try {
            Optional<Album> compare = repository.findById(id);
            if (compare != null && compare.isPresent()) {
                repository.delete(compare.get());
                return new ResponseEntity<>(compare.get(), HttpStatus.OK);
            } else {
                return new ResponseEntity<>(id, HttpStatus.CONFLICT);
            }
        } catch (Exception ex) {
            return new ResponseEntity<>(ex, HttpStatus.INTERNAL_SERVER_ERROR);
        }
    }
}